var collegeContract = new web3.eth.Contract(Collegeabi);

//deploying college contract
var deployedCollege, deployedcert, deployedpop;
let certAddress, popaddress;
collegeContract
  .deploy({
    data: "",
    arguments: [_dean],
  })
  .send(
    {
      from: selectedAccount,
      gas: "4700000",
    },
    (err, transactionHash) => {
      console.log("Transaction Hash :", transactionHash);
    }
  )
  .on("confirmation", () => {})
  .then((newContractInstance) => {
    console.log(
      "Deployed College Contract Address : ",
      newContractInstance.options.address
    );
    deployedCollege = new web3.eth.Contract(
      Collegeabi,
      newContractInstance.options.address
    );

    //getting population address
    deployedCollege.methods
      .populationContract()
      .call({ from: selectedAccount })
      .then(function (result) {
        popaddress = result;
        console.log("population contract address is: ", result);
      });

    //getting cert address
    deployedCollege.methods
      .gradCertifcationTokenAddress()
      .call({ from: selectedAccount })
      .then(function (result) {
        certAddress = result;
        console.log("cert contract address is: ", result);
      });
  });
